package COM.archi.dl.rtf;import java.awt.*;public class State{	RTFDestinationState destinationState = null;	Font font = null;	Color color = null;   		public State( RTFDestinationState state, Font f, Color c )	{		destinationState = state;		font = f;		color = c;	}		public RTFDestinationState getDestinationState()	{		return destinationState;	}		public Font getFont()	{		return font;	}		public Color getColor()	{		return color;	}	} // end class Style