package COM.archi.dl.rtf;import java.util.Vector;import java.io.Serializable;public class StyledDocument implements Serializable{	private Vector styledTextRuns;		public StyledDocument()	{		styledTextRuns = new Vector( 20, 10 );	}		public void addStyledTextRun( StyledTextRun str )	{		styledTextRuns.addElement( str );	}		public Vector getStyledTextRuns()	{		return styledTextRuns;	}	} // end class StyledDocument