/*	Trivial application that displays a string - 4/96 PNL*///package java.mytest;import java.io.*;import java.awt.*;public class Streamline{	final static PrintStream os = System.out;		public void whoosh(String s)	{		os.println("You wrote \""+s+"\"");	}		public void whoosh(int i)	{		os.println("The magic number is " + i + ".");	}}public class DirtyWindow extends Frame{	public DirtyWindow(){ super("Quick & Dirty"); new_window( 300, 150 ); }	public DirtyWindow(int w, int h){ super("Slow & Painful"); new_window( w, h ); }		private void new_window( int width, int height )	{		resize(width,height);		show();	}		public boolean handleEvent(Event evt)	{		if( evt.id == Event.WINDOW_DESTROY )			dispose();		return false;	}	}public class MyTest {	public static void main(String args[]) {		DirtyWindow dw;				dw = new DirtyWindow(640,480);	}}